<?phprequire_once('GraphImage.php');// STEP 1: Construct$graph = new GraphImage(800,300);// STEP 2: Get input$inputArray = array(50,230, 100, 78, 200, 345);$inputArray2 = array(23,30,44,60,98,145);		// STEP 3: Set input$graph->SetInputArray($inputArray);$graph->SetInputArray($inputArray2);// STEP 4: Get image$image = $graph->GetImage(); //must fail if there is no width, height or inputArray// STEP 5: Make .PHP -> .PNG-imageheader('Content-type:image/png');// STEP 6: Display on screenimagepng($image);// STEP 7: Destroy garbageimagedestroy($image);?>