<?phprequire_once('../../includes/master.inc.php');require_once('linegraph.php');if (ClientSession::hasSession(session_id())){	// Data	$citizenData = array(0);	$workerData = array(0);	$width = isset($_REQUEST['width']) ? $_REQUEST['width'] : 480;	$height = isset($_REQUEST['height']) ? $_REQUEST['height'] : 220;		$gameId = Game::getGameIdOfSession(session_id());		$citizenData = LoadCitizenData($gameId, $_REQUEST['station']);	$workerData = LoadWorkerData($gameId, $_REQUEST['station']);	$initCitizenCount = Station::getInitialCitizenCount($_REQUEST['station']);	$initWorkerCount = Station::getInitialWorkerCount($_REQUEST['station']);		if(isset($citizenData))		array_unshift($citizenData, $initCitizenCount);	else		$citizenData = array($initCitizenCount);		if(isset($workerData))		array_unshift($workerData, $initWorkerCount);	else		$workerData = array($initWorkerCount);		// Construct	// $graph = new LineGraph(480,220);	$graph = new LineGraph($width, $height);		// Set input	$graph->SetInputArray($citizenData);	$graph->SetInputArray($workerData);		//var_dump( $citizenData);		// Get image	$image = $graph->GetImage(); //must fail if there is no width, height or inputArray		// Make .PHP -> .PNG-image	header('Content-type:image/png');		// Display on screen	imagepng($image);		// Destroy garbage	imagedestroy($image);}function LoadCitizenData($game_id, $station_id){	if (isset($game_id) && isset($station_id))	{		$db = Database::getDatabase();		$query = "			SELECT				(					(						(							(								Station.area_cultivated_home - 								(									(SUM(Program.area_home) + SUM(Program.area_work) + SUM(Program.area_leisure)) 									* 									(transform_area_cultivated_home / (transform_area_cultivated_home + transform_area_cultivated_work + transform_area_cultivated_mixed + transform_area_undeveloped_urban + transform_area_undeveloped_mixed))								)							)							* 							(count_home_total / area_cultivated_home)						) 						+ 						SUM(Program.area_home * Types.density)					) 					* Constants.average_citizens_per_home				) AS CitizenCount			FROM Constants, Station			INNER JOIN StationInstance ON Station.id = StationInstance.station_id 			INNER JOIN TeamInstance ON StationInstance.team_instance_id = TeamInstance.id			INNER JOIN RoundInstance ON StationInstance.id = RoundInstance.station_instance_id			INNER JOIN Program ON RoundInstance.exec_program_id = Program.id			INNER JOIN Types ON Program.type_home = Types.id			INNER JOIN Round ON RoundInstance.round_id = Round.id AND Station.id = Round.station_id			INNER JOIN RoundInfo ON Round.round_info_id = RoundInfo.id			INNER JOIN RoundInfo AS RoundInfo2 ON RoundInfo.id < RoundInfo2.id			INNER JOIN Game ON TeamInstance.game_id = Game.id AND RoundInfo2.id <= Game.current_round_id			WHERE Game.id = :game_id AND Station.id = :station_id			GROUP BY Station.id, RoundInfo2.id			ORDER BY RoundInfo2.id;";		$args = array('game_id' => $game_id, 'station_id' => $station_id);		$result = $db->query($query, $args);		if (mysql_num_rows($result) > 0)		{			$data = array();			while ($row = mysql_fetch_array($result))				$data[] = round($row['CitizenCount']);			return $data;		}		else			return NULL;	}}function LoadWorkerData($game_id, $station_id){	if (isset($game_id) && isset($station_id))	{		$db = Database::getDatabase();		$query = "			SELECT				(					(						(							(								Station.area_cultivated_work - 								(									(SUM(Program.area_home) + SUM(Program.area_work) + SUM(Program.area_leisure)) 									* 									(transform_area_cultivated_work / (transform_area_cultivated_home + transform_area_cultivated_work + transform_area_cultivated_mixed + transform_area_undeveloped_urban + transform_area_undeveloped_mixed))								)							)							* 							(count_work_total / area_cultivated_work)						) 						+ 						SUM(Program.area_work * Types.density)					) 					* Constants.average_workers_per_bvo				) AS WorkerCount			FROM Constants, Station			INNER JOIN StationInstance ON Station.id = StationInstance.station_id 			INNER JOIN TeamInstance ON StationInstance.team_instance_id = TeamInstance.id			INNER JOIN RoundInstance ON StationInstance.id = RoundInstance.station_instance_id			INNER JOIN Program ON RoundInstance.exec_program_id = Program.id			INNER JOIN Types ON Program.type_work = Types.id			INNER JOIN Round ON RoundInstance.round_id = Round.id AND Station.id = Round.station_id			INNER JOIN RoundInfo ON Round.round_info_id = RoundInfo.id			INNER JOIN RoundInfo AS RoundInfo2 ON RoundInfo.id < RoundInfo2.id			INNER JOIN Game ON TeamInstance.game_id = Game.id AND RoundInfo2.id <= Game.current_round_id			WHERE Game.id = :game_id AND Station.id = :station_id			GROUP BY Station.id, RoundInfo2.id			ORDER BY RoundInfo2.id;";		$args = array('game_id' => $game_id, 'station_id' => $station_id);		$result = $db->query($query, $args);		if (mysql_num_rows($result) > 0)		{			$data = array();			while ($row = mysql_fetch_array($result))				$data[] = round($row['WorkerCount']);			return $data;		}		else			return NULL;	}}?>