<?phprequire_once './includes/master.inc.php';?><?phpfunction ShowFacilityForm(){    $game_id = Game::getGameIdOfSession(session_id());    ?>    <form id="facility-form">    <p class="log">    <?php    $facilities = Facility::getFacilitiesInGame($game_id);    while ($facility = mysql_fetch_array($facilities))    {        echo $facility['Round'] . ' - ' . $facility['Station'] . ' - ' . $facility['Facility'] . '<br/>';    }    ?>    </p>    <select id="bonus-stations">    <?php        $stations = Station::getStationsUsedInGame($game_id);        foreach ($stations as $station)        {            echo '<option value="' . $station->id . '">' . $station->name . '</option>';        }    ?>    </select>    <select id="bonus-bouw">    <?php        $facilities = Facility::getAllFacilities();        foreach ($facilities as $facility)        {            echo '<option value="' . $facility->id . '">' . $facility->name . '</option>';        }    ?>    </select>    <button id="bonus-button" onclick="return buildFacility()">Build</button>    </form>    <?php}function ShowRestrictionForm(){    $game_id = Game::getGameIdOfSession(session_id());    ?>    <form id="restriction-form">    <p class="log">    <?php    include('pages/province/get_restrictions.php');    ?>    </p>    <select id="bonus-stations">    <?php        $stations = Station::getStationsUsedInGame($game_id);        foreach ($stations as $station)        {            echo '<option value="' . $station->id . '">' . $station->name . '</option>';        }    ?>    </select>    <select id="penalty">    <?php        $types = Type::getSpecificTypes();        foreach ($types as $type)        {            echo '<option value="' . $type->id . '">' . $type->name . '</option>';        }    ?>    </select>    <button id="penalty-button" onclick="return addRestriction()">Add Restriction</button>    </form>    <?php}?><html>    <head>        <meta http-equ